using System.Collections.Generic;using DesertImage.ECS;using DesertImage.Pools;using Framework.FX;using UnityEngine;namespace DesertImage{    [CreateAssetMenu(fileName = "FXLibrary", menuName = "Factories/FX Library")]    public class ScriptableFXLibrary : ScriptableObject, IAwake, IInitialComponent    {        public List<FXSpawnNode> Nodes;        private MonoBehaviourPool<EffectBase> _effectPool;        public void OnAwake()        {            var factoryFx = Core.Instance.Get<FactoryFx>();            foreach (var fxSpawnNode in Nodes)            {                factoryFx.Register(fxSpawnNode);            }        }    }}